import { useNavigate, useParams } from "react-router-dom";
import produits from "../../bd-json/produit";
import "./detail.css";
export default function () {
  let { prix } = useParams();
  let { img } = useParams();
  let { categ } = useParams();
  let { id } = useParams();
  let { libelle } = useParams();
  let navigate = useNavigate();
  let myProduct=produits[id];
  let stock=myProduct.stock;
  return (
    <div className="mycontainer  ">
      <div class="row mt-5">
        <img src={`/Img/${img}`} className="imgdetail" />
      </div>
      <div class="row mt-5 ">
        <div class="col-lg-6 col-md-12 col-12 c1 ">
          <h3 className={stock}>{stock} <p className="libelle">|{libelle}</p></h3>
          //


          <p>Prix:{prix}</p>
               <hr/>
               {myProduct.description}
               <hr/>
           <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/produits/${categ}`);
            }}
          >
            Retour
          </button>
          <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/`);
            }}
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
}
