import { BrowserRouter, Routes, Route,Switch } from "react-router-dom";
import {useState} from 'react'
import Home from "../Home/Home";
import Error from "../erreur/Error";
import Produits from "../Produit/produits";
import Contact from "../contact/Contact";
import Détail from "../detail/Detail";
import Login from "../Login/Login"; 
import Panier from '../Panier/Panier'
export default function Router(){
  const [auth,setAuth]=useState(false);
    return(
        <BrowserRouter>
          <Routes>
        {
          auth && 
            <Switch>
          <Route path="/" exact component={<Home />} />
          <Route path="/produits" exact component={<Produits />} />
          <Route path="/produits/:params" exact component={<Produits />} />
          <Route path="/Detail/:id" exact component={<Détail />} />

          <Route path="/Contact" exact component={<Contact />} />
          <Route path="/Panier" exact component={<Panier/>}/>
          <Route component={<Error />} />
        </Switch>
        }
        {
          !auth && <>
          <Route component={<Login />} />  
        </>   
        }           
        </Routes>
        </BrowserRouter>
    )
}
