import { useState } from "react";
import  contacts from '../../bd-json/contact'



export default function Contact() {
  const [message, SetMessage] = useState("");
  const [email,setEmail] = useState("")
  const [data,setData] = useState("")
  const handleMessage = (message,email,event) => {
    event.preventDefault()
    const dataToSend = {email,message}
    setData(dataToSend)
    setEmail("")

    SetMessage("")



   }
  console.log("message: ",data)
  return (
    <div className="container">
      <form onSubmit={(event)=>handleMessage(message,email,event)}>
        <div className="mb-3">
          <label for="email" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            onChange={(event)=>setEmail(event.target.value)}
            placeholder={email}
          />
        </div>
        <div className="mb-3">
          <label for="message" className="form-label">
            Message
          </label>
          <textarea
              onChange={(event)=>SetMessage(event.target.value)}
              placeholder={message}

          className="form-control" id="message"></textarea>
        </div>
         <button type="submit" class="btn btn-primary">
          Envoyer
        </button>
      </form>
      <div style={{display:"flex",flexDirection:"column"}}>
           <p>email : {data.email}</p>
           <p> message : {data.message}</p>
      </div>
    </div>
  );
}
