import "./Panier.css";
import { useNavigate } from "react-router-dom";
export default function Panier() {
  let panier = localStorage.getItem("panier");
  let p = JSON.parse(panier);
  let Navigate = useNavigate();
  let newlist = {
    nbCommqnde: "",
    libelle: "",
    prix: "",
    img: "",
    Quantité: "",
  };
  let handelNavigate = () => {
    Navigate("/");
  };
  let handelAffich = () => {
    let section = document.getElementsByClassName("commander")[0];
    section.setAttribute("class", "affich");
    console.log(section);
  };
  let supprimer = () => {
    let x = document.getElementById("enreg");
    x.setAttribute("class", "commander");
    document.getElementById("commande").setAttribute("disabled", true);

    let sc = document.getElementById("cmd");
    sc.setAttribute("class", "commander");
    console.log(sc);

    localStorage.setItem("panier", JSON.stringify(newlist));
    console.log(section);
  };
  return (
    <div style={{ margin: "100px" }}>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Article</th>
            <th scope="col">Libelle</th>
            <th scope="col">Prix</th>
            <th scope="col">Quantites</th>
            <th scope="col">total</th>
            <th>Supprimer</th>
          </tr>
        </thead>
        <tbody>
          <tr id="enreg">
            <th scope="row">
              <img src={`/Img/${p.img}`} className="imgPanier" />
            </th>
            <td>{p.libelle}</td>
            <td>{p.prix}</td>
            <td>{p.Quantité}</td>
            <th className="total">{p.prix}</th>
            <th>
              <button className="btn btn-danger" onClick={supprimer}>
                Supprimer
              </button>
            </th>
          </tr>
          <tr>
            <td></td>
            <td></td>
            <td colSpan="5">
              <button
                className="btn btn-success"
                id="commande"
                onClick={handelAffich}
              >
                Commander
              </button>
              <button
                className="btn btn-success"
                style={{ width: "120px" }}
                onClick={handelNavigate}
              >
                Home
              </button>
            </td>
          </tr>
        </tbody>
        <section id="cmd" className="commander">
          <div class="mb-3">
            <div class="mb-3">
              <label class="form-label"> address Email</label>
              <input type="email" class="form-control" />
            </div>
            <div class="mb-3">
              <label class="form-label">Num Tél</label>
              <input type="tel" class="form-control" />
            </div>
            <label class="form-label">Paiment</label>
            <select class="form-select">
              <option>espéce</option>
              <option>carte VISA International</option>
              <option>Mastercard</option>
            </select>
          </div>
          <div class="mb-3">
            <label class="form-label">Adresse</label>
            <textarea className="form-control" />
          </div>
          <button className="btn btn-success">Envoyer</button>
        </section>
      </table>
    </div>
  );
}
