import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import "./Produit.css";
import produits from "../../bd-json/produit";
export default function Produits() {
  let { params } = useParams();
  console.log(params)

  return (
    <div className="container">
      {produits.map((data, index) => {
        const { id, libelle, prix, enpromo, catheg, img, description } = data;


        if (catheg == params || params=='tout') {

          return (
          <div>
            <img src={`/Img/${img}`} class="card-img-top" alt="..." />
            <Link
                  to={`/Detail/${catheg}/${libelle}/${enpromo}/${prix}/${img}`}
                  className="nav-link"
                ></Link></div>
          );
        } 

      })}
    </div>
  );
}
