import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import produits from "../../bd-json/produit";
import "./detail.css";
export default function Detail() {
  let { id } = useParams();
  let navigate = useNavigate();
  let myProduct=produits[id];
  let stock=myProduct.stock;
  var qte=document.getElementById('qte');
  const [count,setCount]=useState(0);;
  getcount=()=>{
    return(count);
  }
  return (
    <div className="mycontainer  ">
      <div class="row">
        <img src={`/Img/${myProduct.img}`} className="imgdetail" />
      </div>
      <div className="row  ">
        <div className="col-lg-6 col-md-12 col-12 c1 ">
          <p className="libelle"> {myProduct.libelle} <h4 className={stock}> |{stock} </h4></p>


          <p className="prix">{myProduct.prix} TND</p>
               <hr/>
               {myProduct.description}
               <hr/>
               <h3>Retrait en Magasin ou Livraison Gratuite pour Cet Article</h3>
               <div className="panier">
                 <p>Qt√©: </p><input type="number" min="1" placeholder="1"  id="qte" />
                 <button className="btn btn-success" onClick={()=>setCount(count+1) }>Ajouter au panier</button>
                 {count}
               </div><br/>


           <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/produits/tout`);
            }}
          >
            Retour
          </button>
          <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/`);
            }}
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
}
