import { useNavigate, useParams } from "react-router-dom";
import produits from "../../bd-json/produit";
import "./detail.css";
export default function () {
  let { prix } = useParams();
  let { img } = useParams();
  let { categ } = useParams();
  let { id } = useParams();
  let { libelle } = useParams();
  let navigate = useNavigate();
  let myProduct=produits[id];
  let stock=myProduct.stock;
  return (
    <div className="mycontainer  ">
      <div class="row mt-5">
        <img src={`/Img/${img}`} className="imgdetail" />
      </div>
      <div className="row mt-5 ">
        <div className="col-lg-6 col-md-12 col-12 c1 ">
          <p className="libelle"> {libelle} <h4 className={stock}> |{stock} </h4></p>


          <p className="prix">{prix} TND</p>
               <hr/>
               {myProduct.description}
               <hr/>
               <h3>Retrait en Magasin ou Livraison Gratuite pour Cet Article</h3>
               <div className="panier">
                 <p>Qté: </p><input type="number" min="0" />
               </div><br/>
           <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/produits/${categ}`);
            }}
          >
            Retour
          </button>
          <button className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/`);
            }}
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
}
