import "./Panier.css";
import { useNavigate } from "react-router-dom";
export default function Panier() {
  let panier = localStorage.getItem("panier");
  let p = JSON.parse(panier);
  let Navigate = useNavigate();
  let handelNavigate = () => {
    Navigate("/");
  };
  let handelAffich = () => {
    let section = document.getElementsByClassName("commander")[0];
    section.setAttribute('class','affich');
    console.log(section)
  };
  let supprimer=()=>{
    p.libelle="";
    p.prix="";
    p.Quantité="";
    console.log(p)
} 
 return (
    <div style={{ margin: "100px" }}>
      {p.prix}
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Article</th>
            <th scope="col">Libelle</th>
            <th scope="col">Prix</th>
            <th scope="col">Quantites</th>
            <th scope="col">total</th>
            <th>Supprimer</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">
              {" "}
              <img src={`/Img/${p.img}`} className="imgPanier" />
            </th>
            <td>{p.libelle}</td>
            <td>{p.prix}</td>
            <td>{p.Quantité}</td>
            <th className="total">{p.prix}</th>
            <th><button className="btn btn-danger" onClick={p.libelle=""}>Supprimer</button></th>
          </tr>
          <tr>
            <td></td>
            <td></td>
            <td colSpan="5">
              <button className="btn btn-success" onClick={handelAffich}>
                Commander
              </button>
              <button
                className="btn btn-success"
                style={{ width: "120px" }}
                onClick={handelNavigate}
              >
                Home
              </button>
            </td>
          </tr>
        </tbody>
        <section className="commander">
          <div class="mb-3">
            <div class="mb-3">
              <label class="form-label"> address Email</label>
              <input type="email" class="form-control" />
            </div>
            <div class="mb-3">
              <label class="form-label">Num Tél</label>
              <input type="tel" class="form-control" />
            </div>
            <label class="form-label">Paiment</label>
            <select class="form-select">
              <option>espéce</option>
              <option>carte VISA International</option>
              <option>Mastercard</option>
            </select>
          </div>
          <div class="mb-3">
            <label class="form-label">Adresse</label>
            <textarea className="form-control"/>
          </div>
          <button className="btn btn-success">Envoyer</button>
        </section>
      </table>
    </div>
  );
}
