import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Provider } from "react-redux";
import produits from "../../bd-json/produit";
import Panier from "../../bd-json/panier";
import "./detail.css";
export default function Detail() {
  let { id } = useParams();
  let navigate = useNavigate();
  let myProduct = produits[id];
  let stock = myProduct.stock;
  const [count, setCount] = useState(0);
  const [Quantite,setQuantite]=useState(0);
  // const  saveCommande=(commande)=>{
  //   localStorage.setItem("commande",0);
  // }
  // const getCommande=(val)=>{
  //   localStorage.getItem("commande").push(val);
  // }
  let handleQuantite=(event)=>{
    // qte=event.target.value;
    console.log(event.target)
  }
  function handleAddPanier(e) {
    let id = 0;
    localStorage.setItem("nbCommqnde", count + 1);
    localStorage.setItem("libelle",myProduct.libelle);
    localStorage.setItem("description",myProduct.description);
    localStorage.setItem("Quantité",myProduct.description);

    setCount(count + 1);
  }
  return (
    <div className="mycontainer  ">
      <div class="row">
        <img src={`/Img/${myProduct.img}`} className="imgdetail" />
      </div>
      <div className="row  ">
        <div className="col-lg-6 col-md-12 col-12 c1 ">
          <p className="libelle">
            {" "}
            {myProduct.libelle} <h4 className={stock}> |{stock} </h4>
          </p>

          <p className="prix">{myProduct.prix} TND</p>
          <hr />
          {myProduct.description}
          <hr />
          <h3>Retrait en Magasin ou Livraison Gratuite pour Cet Article</h3>
          <div className="panier">
            <p>Qté: </p>
            <input type="number" min="1" placeholder="1" id="qte" onChange={handleQuantite(event)} />
            <button
              className="btn btn-success"
              onClick={() => handleAddPanier()}
            >
              Ajouter au panier
            </button>
            {count}
          </div>
          <br />

          <button
            className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/produits/tout`);
            }}
          >
            Retour
          </button>
          <button
            className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/`);
            }}
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
}
