import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import "./Produit.css";
import produits from "../bd-json/produit";
export default function Produits() {
  let { params } = useParams();
  return (
    <div className="container">
      {produits.map((data, index) => {
        const { id, libelle, prix, enpromo, catheg, img } = data;
        if (catheg == params) {
          return (
            <div key={id} class="card">
              <div class="imgbox">
                <img src={`/Img/${img}`} class="card-img-top" alt="..." />
                <span class="categorie">{catheg}</span>
              </div>
              <div class="content">
                <h2>card: {index}</h2>
                <p>Prix: {prix}DT</p>
                <Link
                  to={`/Detail/${catheg}/${libelle}/${enpromo}/${prix}/${img}`}
                  className="nav-link"
                >
                  Détail
                </Link>
              </div>
            </div>
          );
        } else if (params == "Produits") {
          return (
            <div key={id} class="card">
              <div class="imgbox">
                <img src={`/Img/${img}`} class="card-img-top" />
                <span class="categorie">{catheg}</span>
              </div>
              <div class="content">
                <h2>card: {index}</h2>
                <p>Prix: {prix}DT</p>
                <Link to={`/Detail/${catheg}/${libelle}/${enpromo}/${prix}/${img}`}className="nav-link">
                  Détail
                </Link>
              </div>
            </div>
          );
        }
      })}
    </div>
  );
}
