import {useNavigate,useParams} from 'react-router-dom'
import './detail.css'
export default function     (){

    let {prix}=useParams();
    let {img}=useParams();
    let {categ}=useParams();
    let  {enpromo}=useParams();
    let navigate=useNavigate();
    return <div className="det">

        <img src={`/Img/${img}`} className="img" /> <p>Prix:{prix}</p><br/>
        En promotion: :< input type="checkbox" checked={enpromo}/><br/>
      <button onClick={()=>{navigate(`/produits/${categ}`)}}>Retour</button>
    </div>
}
