    localStorage.setItem('id',count+1)
    setCount(count + 1);
  }
  return (
    <div className="mycontainer  ">
      <div className="row">
        <img src={`/Img/${myProduct.img}`} className="imgdetail" />
      </div>
      <div className="row  ">
        <div className="col-lg-6 col-md-12 col-12 c1 ">
          <p className="libelle">
            {" "}
            {myProduct.libelle} <h4 className={stock}> |{stock} </h4>
          </p>

          <p className="prix">{myProduct.prix} TND</p>
          <hr />
          {myProduct.description}
          <hr />
          <h3>Retrait en Magasin ou Livraison Gratuite pour Cet Article</h3>
          <div className="panier">
            <p>Qté: </p>
            <input
              type="number"
              min="1" required
              placeholder="1"
              id="qte"
              onChange={handleQuantite}
            />
            <button
              className="btn btn-success"
              onClick={() => handleAddPanier()}
            >
              Ajouter au panier
            </button>
            {count}
          </div>
          <br />

          <button
            className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/produits/tout`);
            }}
          >
            Retour
          </button>
          <button
            className="btn btn-outline-secondary"
            onClick={() => {
              navigate(`/`);
            }}
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
}
