import { useState } from "react";
import  contacts from '../../bd-json/contact'



export default function Contact() {
  const [message, SetMessage] = useState("");
  const [email,setEmail] = useState("")
  const [data,setData] = useState("")
  const handleMessage = (message,email,event) => {
    console.log(message,email)
    event.preventDefault()
    const dataToSend = {email,message}
    setData(dataToSend) 
    localStorage.setItem('emailToSend',dataToSend)
  }
  console.log("message: ",data)
  return (
    <div className="container">
      <form onSubmit={(event)=>handleMessage(message,email,event)}>
        <div className="mb-3">
          <label for="exampleInputEmail1" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            aria-describedby="emailHelp"
            onChange={(event)=>setEmail(event.target.value)}
          />
        </div>
        <div className="mb-3">
          <label for="exampleInputPassword1" className="form-label">
            Message
          </label>
          <textarea
              onChange={(event)=>SetMessage(event.target.value)}

          className="form-control" id="message"></textarea>
        </div>
         <button type="submit" class="btn btn-primary">
          Envoyer
        </button>
      </form>

    </div>
  );
}
