import Slider from "../slider/Slider";
import produits from "../../bd-json/produit";
import "./Home.css";
export default function Home() {
    let i=1;
   function onAffiche(e){
       i=i+1;
       console.log(e)
       let font=document.getElementById("font");
       let elmnt=document.getElementById("text");
       let btn=document.getElementById("btn");
       if(i%2==0){
       btn.innerHTML="Moins <i class='fas fa-angle-up'></i>"
       elmnt.className="azerty";
       font.className="fas fa-angle-up";}
       else if(i%2!=0){
        elmnt.className="card-text";
        btn.innerHTML="Plus <i class='fas fa-chevron-down'></i>"
    }

    }

  return (
    <div>
      <Slider />
      <section >
      <div class="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
        {produits.map((data, index) => {
          return (
            <div class="col">
              <div class="card shadow-sm">
                <img src={`/Img/${data.img}`} className="img_card" />

                <div class="card-body">

                  <div class="d-flex justify-content-between align-items-center">
                    <div class="btn-group">
                    <Link to={`/detail/${data.id}`}
                        type="button"
                        class="btn btn-sm btn-outline-secondary"
                      >
                        détail

                      </Link>
                    </div>
                    <small class="text-muted">9 mins</small>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      </section>
      <container class="container1">
      <div class="card mb-6 c2">
  <img src="https://mk-media.mytek.tn/media/catalog/product/cache/8be3f98b14227a82112b46963246dfe1/b/x/bx80677i37100_1.jpg" class="card-img-top" alt="..."/>
  <div class="card-body">
    <h5 class="card-title">PROCESSEUR</h5>
    <p class="card-text">PROCESSEUR INTEL CORE I3-7100 SOCKET INTEL LGA 1151</p>
    <p class="card-text" id="text">Modèle de processeur: Intel Core i3-7100 7è génération - Support du processeur: 1151 FCLGA - Fréquence de Processeur: 3.9 GHz - Nombre de Coeurs: Dual-Core - Nombre de Threads: 4 - Mémoire Cache: 3 Mo SmartCache - Capacité de Mémoire Max: 64Go - Nombre de Cannaux Mémoire: 2 - Types de mémoire Supportés: DDR4-2133/2400 MHz, DDR3L-1333/1600 MHz - Processeur graphique: Intel HD graphics 630 - Fréquence graphique de base: 350 MHz - Fréquence graphique dynamique maxi: 1.10 GHz - Résolution maximale (HDMI 1.4): 4096x2304 px -</p>
    <button type="button" class="btn " onClick={onAffiche} id="btn">Plus<i class="fas fa-chevron-down" id="font"></i></button>
  </div>
</div>
<div class="card mb-6 c2">
<img src="https://mk-media.mytek.tn/media/catalog/product/cache/8be3f98b14227a82112b46963246dfe1/m/g/mgn93fn-a.jpg" class="card-img-top" alt="..."/>
<div class="card-body">
    <h5 class="card-title">APPLE MACBOOK</h5>
    <p class="card-text">APPLE MACBOOK AIR M1 8GO 256GO SSD - SILVER (MGN93FN-A)</p>
    <p class="card-text" >cran 13" Retina LED IPS (2560 x 1600 pixels) - Processeur: Apple M1 (CPU 8 coeurs / GPU 7 coeurs / Neural Engine 16 coeurs) - Système d'exploitation: MacOS - Mémoire RAM: 8 Go DDR4 - Disque Dur: 256 Go SSD - Carte Graphique: Intel HD Graphics avec Wi-Fi, Bluetooth, 2x Thunderbolt USB4/USBType-C, Prise Casque Jack 3.5mm - Capteur Touch ID, Clavier et trackpad Magic Rétroéclairé - Couleur: Silver - Garantie: 1 an</p>
    <button type="button" class="btn "  >Plus<i class="fas fa-chevron-down" ></i></button>
  </div>
</div>
      </container>

      <footer class="py-3 my-4">
        <ul class="nav justify-content-center border-bottom pb-3 mb-3">
          <li class="nav-item">
            <a href="#" class="nav-link px-2 text-muted">
              <i class="fas fa-home"></i>
            </a>
          </li>
        </ul>
        <p class="text-center text-muted">© 2021 Company, Inc</p>
      </footer>
    </div>
  );
}
